import React from "react";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Box,
  Chip,
  Button,
  Stack,
} from "@mui/material";

export interface RecipeCardProps {
  image: string;
  title: string;
  cookingTime: string;
  difficulty: "Easy" | "Medium" | "Hard";
  dietaryTags: string[];
  onAddToMealPlanner?: () => void;
}

export const RecipeCard = ({
  image,
  title,
  cookingTime,
  difficulty,
  dietaryTags,
  onAddToMealPlanner,
}: RecipeCardProps) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        height="180"
        image={image}
        alt={title}
        sx={{ objectFit: "cover" }}
      />
      <CardContent>
        <Typography gutterBottom variant="h6" component="div">
          {title}
        </Typography>

        <Box sx={{ display: "flex", gap: 2, mb: 1 }}>
          <Typography variant="body2" color="text.secondary">
            ⏱ {cookingTime}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            💪 {difficulty}
          </Typography>
        </Box>

        <Stack direction="row" spacing={1} flexWrap="wrap" mb={2}>
          {dietaryTags.map((tag, index) => (
            <Chip key={index} label={tag} size="small" />
          ))}
        </Stack>

        <Button
          variant="contained"
          color="primary"
          fullWidth
          onClick={onAddToMealPlanner}
        >
          Add to Meal Planner
        </Button>
      </CardContent>
    </Card>
  );
};
